import axios from "axios";
import {CREATE_USER} from '../Constants/constants';

export default function createUser(userData) {
    const dataUser = {
        email: userData.email,
        password: userData.password,
        repeatPass: userData.repeatPass
    
    };
    return (dispatch) => {
      // console.log(BACK_URL);
      console.log('soy dataUser-------------', dataUser)
  
      axios
        .post('http://192.168.0.19:3001/user', dataUser)
        console.log('soy dataUser-------------', dataUser)
        .then((res) => {
          console.log('soy res.data-------------', res.data);
          dispatch({
            type: CREATE_USER,
            users: res.data || {},
            createUserSuccess: true,
          }
          )}
          )}
        }
